<?php$im_id = rand();?><div class="owl-wrapper-rel"><div id="owl-slider-<?php echo $im_id; ?>" class="owl-carousel"><?phpfor($x = 0; $x < $arrlength_carousel; $x++) {$carousel_image_url = wp_get_attachment_image_src( $carousel_items[$x], 'blog-posts' );$carousel_image_title = get_the_title($carousel_items[$x]);if ($carousel_style == "1") {$style = "img-gallery";$circle = "";$circle2 = "";} elseif ($carousel_style == "2") {$style = "img-gallery-rectangle";$circle = "";$circle2 = "";} elseif ($carousel_style == "3") {$style = "img-gallery-circle";$circle = "-circle";$circle2 = "circle-controls";}echo "<div class='img-wrapper".$circle." img-gallery-view'><a href='".esc_url($carousel_image_url[0])."' data-lightbox='gallery-name' data-title='".esc_attr($carousel_image_title)."'><div class='".esc_attr($style)."' style='background-image: url(".esc_url($carousel_image_url[0])."); height: 250px;'><div class='hover-effect'><i class='feather-maximize'></i></div></div></a></div>";}?></div><div class="owl-nav <?php echo $circle2; ?>">  <a class="owl prev"><i class="fa-angle-left"></i></a>  <a class="owl next"><i class="fa-angle-right"></i></a></div></div><script type="text/javascript">      (function($) { "use strict";	  $(document).ready(function() {     var owl = $("#owl-slider-<?php echo $im_id; ?>");    owl.owlCarousel({items: <?php echo esc_js($carousel_image_number); ?>,itemsScaleUp: true,autoPlay: <?php echo esc_js($carousel_timer); ?>,stopOnHover: true}); // Custom Navigation Events  $(".next").click(function(){    owl.trigger('owl.next');  })  $(".prev").click(function(){    owl.trigger('owl.prev');  })});  })(jQuery);</script>	