<div class="row img-gallery-view"><?phpfor($x = 0; $x < $arrlength_gallery; $x++) {$single_image_url = wp_get_attachment_image_src( $gallery_items[$x], 'blog-posts' );$image_title = get_the_title($gallery_items[$x]);if ($gallery_style == "1") {$style = "img-gallery";$wrapper = "";} elseif ($gallery_style == "2") {$style = "img-gallery-rectangle";$wrapper = "";} elseif ($gallery_style == "3") {$style = "img-gallery-circle";$wrapper = "-circle";}if ($gallery_layout_style == "1") {$col = "col-sm-6";} elseif  ($gallery_layout_style == "2")  {$col = "col-sm-4";} elseif  ($gallery_layout_style == "3")  {$col = "col-sm-3";} elseif  ($gallery_layout_style == "4")  {$col = "col-md-2";}echo "<div class='".$col."'><div class='img-wrapper".$wrapper."'><a href='".esc_url($single_image_url[0])."' data-lightbox='gallery-name' data-title='".esc_attr($image_title)."'><div class='".$style."' style='background-image: url(".esc_url($single_image_url[0])."); height: 250px;'><div class='hover-effect'><i class='feather-maximize'></i></div></div></a></div></div>";}?></div>