<?php/** * The header for our theme. * * Displays all of the <head> section and everything up till <div id="content"> * * @package skyline */$skyline_data = skyline_redux_data();// Determine Navigation Style // if ($skyline_data['nav_type'] == "1") { require_once(SKYLINE_INCLUDES_PATH . '/headers/header1.php');} elseif ($skyline_data['nav_type'] == "2") { require_once(SKYLINE_INCLUDES_PATH . '/headers/header2.php');} elseif ($skyline_data['nav_type'] == "3") { require_once(SKYLINE_INCLUDES_PATH . '/headers/header3.php');} elseif ($skyline_data['nav_type'] == "4") { require_once(SKYLINE_INCLUDES_PATH . '/headers/header4.php');} elseif ($skyline_data['nav_type'] == "5") { require_once(SKYLINE_INCLUDES_PATH . '/headers/header5.php');} elseif ($skyline_data['nav_type'] == "6") { require_once(SKYLINE_INCLUDES_PATH . '/headers/header6.php');} else { require_once(SKYLINE_INCLUDES_PATH . '/headers/header5.php');}// PAGE LOADER OPTIONS// MUST LOAD HERE PRIOR TO ANYTHING ELSE$page_loader = $skyline_data['page_loader'];if ($page_loader == 1) { ?><style type="text/css">#pageloader {display: block;background-color: <?php echo $skyline_data['page_loader_bg_color']; ?> !important;}.spinner > div {  background-color: <?php echo $skyline_data['page_loader_color']; ?> !important;}</style><?php} elseif ($page_loader == 0) { ?><style type="text/css">#pageloader {display: none !important;}</style><?php }?>