<?php/** * The template for displaying all pages. * * This is the template that displays all pages by default. * Please note that this is the WordPress construct of pages * and that other 'pages' on your WordPress site will use a * different template. * * @package skyline  */$page_id = get_the_ID();$vc_enabled = get_post_meta($page_id, '_wpb_vc_js_status', true);if ($vc_enabled == "true") {get_template_part( 'page-vc' );} elseif ($vc_enabled == "false") {get_template_part( 'page-no-vc' );} elseif ($vc_enabled == "") {get_template_part( 'page-no-vc' );}?>